$(document).ready(function () {
    createPopup();
    $(".popup-close").click(function () {
        $(".popup-overlay").fadeOut()
    });
    checklocalStorage();
    $(document).on("click", function (a) {
        if ($(a.target).parents(".popup-overlay").size() == 0 && (!$(a.target).is(".popup-close"))) {
            $(".popup-overlay").fadeOut()
        }
    })
});
function checklocalStorage() {
    if (!fetch_fromLocalStorage("popup")) {
        save_ToLocalStorage("popup", "shown");
        $(".popup-overlay").fadeIn()
    }
} function createPopup() {
    $("body").append('<div class="popup-overlay"><div class="popup-wrap"><a href="#" class="popup-close"><img src="/Home_files/close.png"></a><div class="modal-popup"><img src="/Home_files/CM-Mobile-App-Handout_English.jpg"></div></div></div>')
} function save_ToLocalStorage(a, b) {
    b = JSON.stringify(b);
    localStorage.setItem(a, b)
} function fetch_fromLocalStorage(a) {
    var b = localStorage.getItem(a);
    return JSON.parse(b)
};